  "zones": [
    {"tier": 1, "range": "<6 hours", "score": 0, "description": "Insufficient sleep - serious health risks"},
    {"tier": 2, "range": "6-6.9 hours", "score": 25, "description": "Below optimal - increased health risks"},
    {"tier": 3, "range": "7-9 hours", "score": 100, "description": "Optimal sleep duration for health"},
    {"tier": 4, "range": "9.1-10 hours", "score": 75, "description": "Above optimal but still acceptable"},
    {"tier": 5, "range": ">10 hours", "score": 25, "description": "Excessive - may indicate underlying issues"}
  ]
}

